import { Component } from '@angular/core';
import axios from 'axios';

@Component({
  selector: 'app-adminsection',
  templateUrl: './adminsection.component.html',
  styleUrls: ['./adminsection.component.css']
})
export class AdminsectionComponent {

  title = 'mezzoapp';
  data = []
  checkview=false
  ngOnInit():void { this.fn()}


  hassen =""
  condition=false
  password = ""
  date = ""
  age = ""
  email = ""
  email1 = ""
  Active = ""
  firstname = ""
  lastname = ""
  role=""
  Getrole(event:any){
    this.role=event.target.value
  }
  GetPassword(event:any){
    this.password= event.target.value
    }
    Getfirstname(event:any){
      this.firstname= event.target.value}

      Getlastname(event:any){
        this.lastname= event.target.value}

        Getemail(event:any){
          this.email= event.target.value}

          Getemail1(event:any){
            this.email1= event.target.value}
          Getactive(event:any){
            this.Active= event.target.value}

            Getage(event:any){
              this.age= event.target.value}

              Getdate(event:any){
                this.date= event.target.value}

fn(){
  axios.get('http://localhost:8383/Utilisateur/AllUtilisateurs').then((res)=>{this.data=Object.values(res.data),console.log(res.data,"lol")}).then((err)=>console.log(err))
}

fndel(id: any){

  axios.delete(`http://localhost:8383/Utilisateur/${id}`).then((res)=>this.fn()).then((err)=>console.log(err))
}

fnupdate(id :any){
  this.checkview=!this.checkview
  this.hassen=id
  this.condition == true
}
fnupdate1(){

  axios.put(`http://localhost:8383/Utilisateur/UpdateUtilisateur`,{id:this.hassen,email:this.email1}).then((res)=>this.fn()).then((err)=>console.log(err))
}

}
